    "Kerroin kollegoilleni kohtelusta",
    "Jäin pois toiminnasta, jätin menemättä tehtäviin",
    "Ilmoitin asiasta luottamushenkilölle tai työsuojeluun",
    "Ilmoitin asiasta johtohenkilölle, ohittaen esihenkilöni"
  ), str_wrap, width = 38, USE.NAMES = FALSE
)

reaktiot.kaikki &lt;- data |&gt;
  filter(!(k22_1 == -1)) |&gt;
  select(k23_1:k23_11) |&gt;
  pivot_longer(
    cols = k23_1:k23_11,
    names_to = "variable",
    values_to = "value"
  ) |&gt;
  group_by(variable, value) |&gt;
  summarise(count = n()) |&gt;
  mutate(prop = count/sum(count))

jarjestys.reaktiot &lt;- reaktiot.kaikki |&gt; pull(variable)

vanhat_nimet &lt;- data |&gt;
  select(k23_1:k23_11) |&gt;
  colnames()

nimet &lt;- setNames(
  as.list(nimet),
  vanhat_nimet
)[jarjestys.reaktiot]

reaktiot.jarj &lt;- reaktiot.kaikki |&gt;
  arrange(desc(prop)) |&gt;
  mutate(
    variable = map_chr(
      variable,
      ~ nimet[[.x]]
    ),
    value = factor(
      value,
      labels = "Kyllä"
    )
  ) |&gt;
  filter(!is.na(value))

### Kuvio
reaktiot.jarj |&gt;
  ggplot(aes(
    x = prop,
    y = reorder(variable, prop),
    fill = value
  )) +
  geom_bar(stat = "identity") +
