22.8 Muutosaikeiden syyt


Kuvion piirtäminen
### Järjestyksen rakentaminen
nimet &lt;- sapply(c(
    "Epäasiallinen tai väkivaltainen kohtelu",
    "Heikko johtaminen",
    "Heikot työehdot",
    "Henkilökohtaiset syyt",
    "Jatkuvuuden epävarmuus",
    "Liikaa vaatimuksia",
    "Ongelmat työnteossa",
    "Ongelmat työyhteisössä",
    "Organisaation rakenneongelmat",
    "Osaamisen kehittäminen",
    "Puutteet arvostuksessa tai tuessa",
    "Puutteet etenemismahdollisuuksissa",
    "Puutteet motivaatiossa",
    "Resurssipula",
    "Työkuormitus",
    "Työnkuvan muutos",
    "Urakehitys",
    "Vuorotyö"
  ), str_wrap, width = 38, USE.NAMES = FALSE
)

syyt.kaikki &lt;- data |&gt;
  filter(!is.na(k43_1)) |&gt;
  select(k43_1:k43_18) |&gt;
  pivot_longer(
    cols = k43_1:k43_18,
    names_to = "variable",
    values_to = "value"
  ) |&gt;
  group_by(variable, value) |&gt;
  summarise(count = n()) |&gt;
  mutate(prop = count/sum(count))

jarjestys.syyt &lt;- syyt.kaikki |&gt; pull(variable)

vanhat_nimet &lt;- data |&gt;
  select(k43_1:k43_18) |&gt;
  colnames()

nimet &lt;- setNames(
  as.list(nimet),
  vanhat_nimet
)[jarjestys.syyt]

syyt.jarj &lt;- syyt.kaikki |&gt;
  filter(value == 1) |&gt;
  arrange(desc(prop)) |&gt;
  mutate(
    variable = map_chr(
      variable,
      ~ nimet[[.x]]
    ),
    value = factor(
      value,
      labels = "Kyllä"
    )
  ) |&gt;
  filter(!is.na(value))

### Kuvio
syyt.jarj |&gt;
  ggplot(aes(
    x = prop,
    y = reorder(variable, prop),
    fill = value
  )) +
  geom_bar(stat = "identity") +
  scale_x_continuous(labels = scales::label_percent(
    suffix = " %",
    decimal.mark = ","
  )) +
  scale_fill_manual(
    values = "#C20E1A"
  ) +
  labs(
    x = "Suhde muutosaikeiden ilmoittaneista vastaajista (%)",
    fill = "Vastaukset"
  ) +
  theme(axis.title.y = element_blank(), legend.position = "none") +
  geom_label(
    aes(
      label = scales::percent(prop, accuracy = 0.1, decimal.mark = ",")
    ),
    position = position_stack(vjust = 0.5),
    show.legend = FALSE,
    fill = "#ffffff"
  )





